import React from "react";
import { Link } from "react-router-dom";
import styles from "./Card.module.css";
import { addFav, removeFav, removePersonaje } from "../redux/actions";
import { connect } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";

function Card({
  id,
  name,
  status,
  species,
  gender,
  origin,
  image,
  onClose,
  showCloseButton = true,
  addFav,
  removeFav,
  myFavorites,
  removePersonaje,
}) {
  const handleRemove = (id) => {
    onClose(id);
    removePersonaje(id);
  };
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      removeFav(id);
      setIsFav(false);
    } else {
      addFav({
        id,
        name,
        status,
        species,
        gender,
        origin,
        image,
      });
      setIsFav(true);
    }
  };
  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [myFavorites]);

  return (
 
<div className={styles.card_hover}>
    {showCloseButton && (
      <button className="closeButton" onClick={() => handleRemove(id)}>
        X
      </button>
    )}
    <div className={styles.card_hover_extra}>
      <div className="favorite">
        {isFav ? (
          <button onClick={handleFavorite} className="favorite">
            ‚ù§Ô∏è
          </button>
        ) : (
          <button onClick={handleFavorite} className="favorite">
            ü§ç
          </button>
        )}
      </div>
      <div className={styles.card_hover_imageContainer}>
        <img className={styles.card_hover_image} src={image} alt="" />
      </div>
      <div className={styles.card_hover_content}>
        <Link className={styles.card_hover_link} to={`/detail/${id}`}>
          <h2 className={styles.card_hover_title} data-name={name}>
            {name}
          </h2>
        </Link>
        <div className={styles.card_hover_details}>
          <h3 className={styles.card_hover_subtitle}>{status}</h3>
          <h3 className={styles.card_hover_subtitle}>{species}</h3>
          <h3 className={styles.card_hover_subtitle}>{gender}</h3>
          <h3 className={styles.card_hover_subtitle}>{origin}</h3>
        </div>
      </div>
    </div>
  </div>

    // <div classNameName={styles.Card__item}>
    //   {showCloseButton && (
    //     <button classNameName={styles.closeButton} onClick={() => handleRemove(id)}>
    //       X
    //     </button>
    //   )}
    //   <div classNameName={styles.favorite}>
    //     {isFav ? (
    //       <button onClick={handleFavorite} classNameName={styles.favorite}>
    //         ‚ù§Ô∏è
    //       </button>
    //     ) : (
    //       <button onClick={handleFavorite} classNameName={styles.favorite}>
    //         ü§ç
    //       </button>
    //     )}
    //   </div>
    //   <div classNameName={styles.Card__imageContainer}>
    //     <img classNameName={styles.Card__image} src={image} alt="" />
    //   </div>
    //   <div classNameName={styles.Card__info}>
    //     <Link classNameName={styles.Card__link} to={`/detail/${id}`}>
    //       <h2 classNameName={styles.Card__title} data-name={name}>
    //         {name}
    //       </h2>
    //     </Link>
    //     <div classNameName={styles.Card__details}>
    //       <h3 classNameName={styles.Card__subtitle}>{status}</h3>
    //       <h3 classNameName={styles.Card__subtitle}>{species}</h3>
    //       <h3 classNameName={styles.Card__subtitle}>{gender}</h3>
    //       <h3 classNameName={styles.Card__subtitle}>{origin}</h3>
    //     </div>
    //   </div>
    // </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (character) => {
      dispatch(addFav(character));
    },
    removeFav: (id) => {
      dispatch(removeFav(id));
    },
    removePersonaje: (id) => dispatch(removePersonaje(id)),
  };
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
