import React from "react";
import { useState } from "react";


const Form = () => {
    const [userData, setUserData] = useState({
        email: "",
        password: "",
    });
    const [errors, setErrors] = useState({});

    const handleOnChange = (event) => {
        const { name, value } = event.target;
        setUserData({
          ...userData,
          [name]: value
        });
        const newErrors = validate({
          ...userData,
          [name]: value
        });
        setErrors(newErrors);
      };

    // const handleOnChange = (event) => {
    //     setUserData({
    //         ...userData,
    //         [event.target.name]: event.target.value});
            
    //     setErrors(validate({
    //         ...userData,
    //         [event.target.name]: event.target.value
    //     }));
            
    // }

    return (
        <form action="">
            <label htmlFor="email">EMAIL</label>
            <input type="email" name="email" value={userData.email} onChange={handleOnChange}/>
            {errors.email && <p>{errors.email}</p>}
            <label htmlFor="password">PASSWORD</label>
            <input type="password" name="password" value={userData.password} onChange={handleOnChange}/>
            {errors.password && <p>{errors.password}</p>}
            <button>SUBMIT</button>
        </form>

    );
};
const validate = (userData) => {
    const errors = {};
  
    if (!userData.email) {
      errors.email = "El campo de correo electrónico es obligatorio";
    } else if (!/\S+@\S+\.\S+/.test(userData.email)) {
      errors.email = "Debes ingresar un correo electrónico válido";
    } else if (userData.email.length > 35) {
      errors.email = "El correo electrónico debe tener menos de 35 caracteres";
    }
  
    if (!/\d/.test(userData.password)) {
      errors.password = "La contraseña debe incluir al menos un número";
    } else if (!/.{6,10}/.test(userData.password)) {
      errors.password = "La contraseña debe tener entre 6 y 10 caracteres";
    }
  
    return errors;
  };
  
  export default Form;
