import React from "react";
import { useState } from "react";
import {validate} from "./validation"
import style from "./Form.module.css";


const Form = ({login}) => {
    const [userData, setUserData] = useState({
        email: "",
        password: "",
    });
    const [errors, setErrors] = useState({});
    const [hasErrors, setHasErrors] = useState(false); // Agregar estado para verificar si hay errores y utilizarlo para desabilitar el boton de submit

    
    const handleOnChange = (event) => {
        setUserData({
            ...userData,
            [event.target.name]: event.target.value
        });
            
        setErrors(validate({
            ...userData,
            [event.target.name]: event.target.value
        }));
        setHasErrors(
            Object.values(errors).some((error) => error !== "") )// Verificar si hay errores
    }

    const handleSumbit = (event) => {
        event.preventDefault();
        login(userData);
    }


    return (
        <div>
        <form onSubmit={handleSumbit}>
            <label htmlFor="email">EMAIL</label>
            <input type="email" name="email" value={userData.email} onChange={handleOnChange}/>
            {errors.email && <p>{errors.email}</p>}
            <hr />
            <label htmlFor="password">PASSWORD</label>
            <input type="text" name="password" value={userData.password} onChange={handleOnChange}/>
            {errors.password && <p>{errors.password}</p>}
            <hr />
            <button disabled={hasErrors}>SUBMIT</button>
        </form>
        </div>
    );
};
  
  export default Form;
