import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import style from "./Detail.module.css";

const Detail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState({});
  const URL_BASE = "https://be-a-rym.up.railway.app/api/character";
  const API_KEY = "2a5b6e80e805.066223a89f6c5f71a784";
  useEffect(() => {
    axios(`${URL_BASE}/${id}?key=${API_KEY}`).then((response) => {
      if (response.data.id) {
        setCharacter(response.data);
      } else {
        window.alert("No hay personajes con ese ID");
      }
    });
    return setCharacter({});
  }, [id]);

  return (
<div className={style.character_details}>
  {character.name ? (
    <>
      <h2>{character.name}</h2>
      <div class="character_status">
        <span className={character.status === "Alive" ? style.alive : style.dead}></span>
        <h3 className={character.status === "Alive" ? style.status_alive : style.status_death}>{character.status}</h3>
      </div>
      <p>{character.species}</p>
      <p>{character.gender}</p>
      <p>{character.origin?.name}</p>
      <img src={character.image} alt="img" />
    </>
  ) : (
    <div className={style.loader}></div>
  )}
</div>

  );
};


export default Detail;
