import React from "react";
import { Link } from "react-router-dom";
import styles from "./Card.module.css";
import { addFav, removeFav } from "../redux/actions";
import { connect } from "react-redux";
import { useState } from "react";

export function Card({
  id,
  name,
  status,
  species,
  gender,
  origin,
  image,
  onClose,
  addFav,
  removeFav
}){
  const [isFav, setIsFav] = useState(false); 

  const handleFavorite = () => {
    if (isFav) {
      removeFav(id);
      setIsFav(false);
    } else {
      addFav({
        id,
        name,
        status,
        species,
        gender,
        origin,
        image,
      });
      setIsFav(true);
    }
  };




  return (

     
   
    <div className={styles.Card__item}>
          <div>
    {isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
   ) : (
      <button onClick={handleFavorite}>ü§ç</button>
   )}
   </div>
      <button className={styles.Card__close} onClick={() => onClose(id)}>
        X
      </button>
      <img className={styles.Card__image} src={image} alt="" />
      <Link className={styles.Card__link} to={`/detail/${id}`}>
        <h2 className={styles.Card__title} data-name={name}> {/* Agregamos el atributo data-name para mostrar en el tooltip */}
          {name}
        </h2>
      </Link>
      <div>
        <h3 className={styles.Card__subtitle}>{status}</h3> {/* Agregamos la clase para aplicar el sombreado */}
        <h3 className={styles.Card__subtitle}>{species}</h3>
        <h3 className={styles.Card__subtitle}>{gender}</h3>
        <h3 className={styles.Card__subtitle}>{origin}</h3>
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {  
   return {  
              addFav: (character) => { dispatch(addFav(character)) },     
              removeFav: (id) => { dispatch(removeFav(id)) }
    } 
}

export default connect( mapDispatchToProps, null)(Card);
