import React from "react";
import { Link } from "react-router-dom";

export default function Card({
  id,
  name,
  status,
  species,
  gender,
  origin,
  image,
  onClose,
}) {
  return (
   <div className="container">
    <div className="card">
      <button className="close-button" onClick={() => onClose(id)}>
        X
      </button>
      <Link to={`/detail/${id}`}>
        <h2 className="tooltip">
          {name}
          <span className="tooltiptext">¿Quieres saber más sobre {name}?</span>
        </h2>
      </Link>
      <div className="details">
        <h3>{status}</h3>
        <h3>{species}</h3>
        <h3>{gender}</h3>
        <h3>{origin}</h3>
      </div>
      <img className="image" src={image} alt="" />
      <style>
        {`
         .card {
            width: 100%;
            max-width: 600px;
            cursor: pointer;
            transition: transform 0.2s ease-out;
          }
          
          .card:hover {
            transform: translateY(-5px);
          }
          
          .name {
            margin: 0;
            font-size: 24px;
            font-weight: bold;
            text-align: center;
            margin-bottom: 10px;
          }
          
          .details {
            display: flex;
            flex-direction: column;
            align-items: center;
            margin-bottom: 10px;
          }
          
          .details h3 {
            margin: 0;
            font-size: 18px;
            font-weight: bold;
            text-align: center;
            margin-bottom: 5px;
          }
          .tooltip {
            position: relative;
          }
          
          .tooltip .tooltiptext {
            font-size: 14px; 
            visibility: hidden;
            width: 300px;
            background-color: black;
            color: white;
            text-align: center;
            border-radius: 6px;
            padding: 5px 0;
            position: absolute;
            z-index: 1;
            bottom: 100%;
            left: 50%;
            margin-left: -100px;
            opacity: 0;
            transition: opacity 0.90s;
          }
          
          .tooltip:hover .tooltiptext {
            visibility: visible;
            opacity: 1;
          }
          
          
          .image {
            width: 100%;
            height: auto;
            margin-bottom: 10px;
          }
          
          .close-button {
            position: absolute;
            top: 10px;
            right: 10px;
            background-color: #ccc;
            border: none;
            font-size: 16px;
            cursor: pointer;
            padding: 5px 10px;
            border-radius: 50%;
            transition: background-color 0.3s ease-in-out;
          }
          
          .close-button:hover {
            background-color: #999;
          }
        `}
      </style>
    </div>
   </div>
  );
}
