import {useState} from 'react';

export default function SearchBar({onSearch}) {

   const [id, setId] = useState('');
   const [random, setRandom] = useState(Math.floor(Math.random() * 826) + 1);

   const handleChange = (event) => {
      const value = event.target.value;
      const randomValue = event.target.value;
      if (value < 1 || value > 826) {
        alert('Comienza por 1 y busca hasta el 826!');
        event.target.value = '';
         return;
      }
      setId(value);
      setRandom(randomValue);
    };

   return (
      <div>
         <input type='text' onChange={handleChange}/>
         <button onClick={()=>{onSearch(id)}}>Agregar personajes</button>
         <button onClick={()=>{onSearch(random)}}>Obtener personaje aleatorio</button>
      </div>
         );
}
