import React from 'react';
import { Link } from 'react-router-dom';


export default function Card({id, name, status, species, gender, origin, image, onClose}) {
   return (
      <div className="card">
      <button className="close-button" onClick={() => onClose(id)}>X</button>
      <Link to={`/detail/${id}`}>
      <h2 title={`¿Quieres saber más sobre ${name}?`}>{name}</h2>
      </Link>
      <div className="details">
        <h3>{status}</h3>
        <h3>{species}</h3>
        <h3>{gender}</h3>
        <h3>{origin}</h3>
      </div>
      <img className="image" src={image} alt="" />
      <style>
        {`
          .card {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            background-color: #fff;
            border: 1px solid #ccc;
            box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
            padding: 20px;
            width: 300px;
            position: relative;
          }
          
          .name {
            margin: 0;
            font-size: 24px;
            font-weight: bold;
            text-align: center;
            margin-bottom: 10px;
          }
          
          .details {
            display: flex;
            flex-direction: column;
            align-items: center;
            margin-bottom: 10px;
          }
          
          .details h3 {
            margin: 0;
            font-size: 18px;
            font-weight: bold;
            text-align: center;
            margin-bottom: 5px;
          }
          
          .image {
            width: 100%;
            height: auto;
            margin-bottom: 10px;
          }
          
          [title]::after {
            content: attr(title);
            background-color: #333;
            color: #fff;
            padding: 10px;
            font-size: 14px;
            position: absolute;
            z-index: 1;
            left: 50%;
            transform: translateX(-50%);
            white-space: nowrap;
            opacity: 0;
            transition: opacity 0.3s ease-in-out;
          }
          
          [title]:hover::after {
            opacity: 1;
          }
          
          .close-button {
            position: absolute;
            top: 10px;
            right: 10px;
            background-color: #ccc;
            border: none;
            font-size: 16px;
            cursor: pointer;
            padding: 5px 10px;
            border-radius: 50%;
            transition: background-color 0.3s ease-in-out;
          }
          
          .close-button:hover {
            background-color: #999;
          }
        `}
      </style>
    </div>
   );
}
