import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import style from "./Detail.module.css";

const Detail = () => {
  const { id } = useParams();
  const [character, setCharacter] = useState({});
  const URL_BASE = "https://be-a-rym.up.railway.app/api/character";
  const API_KEY = "2a5b6e80e805.066223a89f6c5f71a784";
  useEffect(() => {
    axios(`${URL_BASE}/${id}?key=${API_KEY}`).then((response) => {
      if (response.data.id) {
        setCharacter(response.data);
      } else {
        window.alert("No hay personajes con ese ID");
      }
    });
    return setCharacter({});
  }, [id]);

  useEffect(() => {
   const animatedH2 = document.getElementById('animated-h2');
   const animatedH3 = document.getElementById('animated-h3');
   
   animatedH2.addEventListener('animationend', () => {
     animatedH3.style.animation = 'typing 2s steps(20) forwards';
   });
 }, []);

  return (
<div className={style.character_details}>
  {character.name ? (
    <>
      <h2 id="animated-h2">Name:</h2>
      <h3 id="animated-h3">{character.name}</h3>
      <div className={style.character_status}>
      <h4 className={character.status === "Alive" ? style.status_alive : style.status_death}>{character.status}</h4>
        <span className={character.status === "Alive" ? style.alive : style.dead}></span>
      </div>
      <p>Specie: {character.species}</p>
      <p>Gender: {character.gender}</p>
      <p>Origin: {character.origin?.name}</p>
      <img src={character.image} alt="img" />
    </>
  ) : (
    <div className={style.loader}></div>
  )}
</div>

  );
};
       


export default Detail;
