import React from "react";
import { Link } from "react-router-dom";
import { addFav, removeFav, removePersonaje } from "../redux/actions";
import { connect } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";
import styles from "./Card.module.css";

function Card({
  id,
  name,
  status,
  species,
  gender,
  origin,
  image,
  onClose,
  showCloseButton = true,
  addFav,
  removeFav,
  myFavorites,
  removePersonaje,
}) {
  const handleRemove = (id) => {
    onClose(id);
    removePersonaje(id);
  };
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      removeFav(id);
      setIsFav(false);
    } else {
      addFav({
        id,
        name,
        status,
        species,
        gender,
        origin,
        image,
      });
      setIsFav(true);
    }
  };
  useEffect(() => {
    myFavorites.forEach((fav) => {
      if (fav.id === id) {
        setIsFav(true);
      }
    });
  }, [myFavorites]);

  return (
    <div className={styles.container}>
        
              {showCloseButton && (
                <button
                  className={styles.closeButton}onClick={() => handleRemove(id)}>
                </button>
              )}
                {isFav ? (
                  <button onClick={handleFavorite} className={styles.favorite}>
                    ‚ù§Ô∏è
                  </button>
                ) : (
                  <button onClick={handleFavorite} className={styles.favorite}>
                    ü§ç
                  </button>
                )}
            <div className={styles.card}>
                <img className={styles.Card__image} src={image} alt="" />
                <h2 >{name}</h2>
              
                 
                <Link className={styles.Card__link} to={`/detail/${id}`}>

                </Link>
                
                <h3 className={styles.Card__subtitle}>{status}</h3>
                <h3 className={styles.Card__subtitle}>{species}</h3>
                <h3 className={styles.Card__subtitle}>{gender}</h3>
                <h3 className={styles.Card__subtitle}>{origin}</h3>
               
              </div>
          
        
    </div>

      
         

  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (character) => {
      dispatch(addFav(character));
    },
    removeFav: (id) => {
      dispatch(removeFav(id));
    },
    removePersonaje: (id) => dispatch(removePersonaje(id)),
  };
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
