import './App.css';
import Cards from './components/Cards.jsx';
import Nav from './components/Nav';
import About from './components/About';
import Detail from './components/Detail.jsx';
import {useState} from "react";
import axios from 'axios';
import { Routes , Route } from 'react-router-dom';
import Error404 from './components/Error404';
import Form from './components/Form';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';

function App() {
   const location = useLocation();
   const [characters, setCharacters] = useState([]);
   const navigate = useNavigate();
   const [access, setAccess] = useState(false);
   const EMAIL = 'max@max.com';
   const PASSWORD = 'maxtobio123';

   function login(userData) {
      if (userData.password === PASSWORD && userData.email === EMAIL) {
         setAccess(true);
         navigate('/home');
      } else {
         window.alert('¡Usuario o contraseña incorrectos!');
      }
   }
 function logout() {
      setAccess(false);
      navigate('/');
   }  

   useEffect(() => {
    !access && navigate('/');
   }, [access]);
 

   function onSearch (id){
      const URL_BASE = 'https://be-a-rym.up.railway.app/api/character';
      const API_KEY = '2a5b6e80e805.066223a89f6c5f71a784';

      if (characters.find((char) => char.id === id)){
         return window.alert('¡Mmm , ese personaje ya lo buscaste!');
        }
      axios(`${URL_BASE}/${id}?key=${API_KEY}`).then(({ data }) => {
         if (data.name) {
          setCharacters((oldChars) => [...oldChars, data]);
         } else {
          return window.alert('¡Algo salió mal!');
         }
      });
     }
   

   const onClose = (id) => {
      setCharacters(characters.filter((char) => char.id !== id));
      };
      
   
   return (
      <div className='App'>
         {location.pathname !== '/' && <Nav onSearch= {onSearch} logout={logout}/>}
         <Routes>
         <Route
          path='/home' element={<Cards characters= {characters} onClose= {onClose}/>}
          /> 
         <Route path='/about' element={<About/>}/>
            <Route path='/detail/:id'element={<Detail/>}/>
            <Route path='*' element={<Error404/>}/>
            <Route path='/' element={<Form login={login}/>}/>
         </Routes>
      </div>
   );
}
        
         


export default App;
