import React from "react";
import { Link } from "react-router-dom";
import styles from "./Card.module.css";
import { addFav, removeFav } from "../redux/actions";
import { connect } from "react-redux";
import { useState } from "react";
import { useEffect } from "react";

export function Card({
 characters,
  onClose,
  addFav,
  removeFav,
  myFavorites,
  
}) {
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      removeFav(id);
      setIsFav(false);
    } else {
      addFav({
      characters,
      });
      setIsFav(true);
    }
  };

  useEffect(() => {
    myFavorites.forEach((fav) => {
       if (fav.id === characters.id) {
          setIsFav(true);
       }
    });
 }, [myFavorites]);

  return (
    <div className={styles.Card__item}>
      <div>
        {isFav ? (
          <button onClick={handleFavorite}>‚ù§Ô∏è</button>
        ) : (
          <button onClick={handleFavorite}>ü§ç</button>
        )}
      </div>
      <button className={styles.Card__close} onClick={() => onClose(id)}>
        X
      </button>
      <img className={styles.Card__image} src={characters.image} alt="" />
      <Link className={styles.Card__link} to={`/detail/${id}`}>
        <h2 className={styles.Card__title} data-name={characters.name}>
          {" "}
          {/* Agregamos el atributo data-name para mostrar en el tooltip */}
          {characters.name}
        </h2>
      </Link>
      <div>
        <h3 className={styles.Card__subtitle}>{characters.status}</h3>{" "}
        {/* Agregamos la clase para aplicar el sombreado */}
        <h3 className={styles.Card__subtitle}>{characters.species}</h3>
        <h3 className={styles.Card__subtitle}>{characters.gender}</h3>
        <h3 className={styles.Card__subtitle}>{characters.origin.name}</h3>
      </div>
    </div>
  );
}

export const mapDispatchToProps = (dispatch) => {
  return {
    addFav: (character) => {
      dispatch(addFav(character));
    },
    removeFav: (id) => {
      dispatch(removeFav(id));
    },
  };
};

export const mapStateToProps = (state) => {
  return {
    favs: state.favs,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Card);
