import { removeFav,addFav } from "./actions";

const initialState = {
  myFavorites: []
};


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'ADD_FAV':
        return {
            ...state,
            myFavorites: [...state.myFavorites, action.payload]
        };
        case 'REMOVE_FAV':
            return {
              ...state,
              myFavorites: state.myFavorites.filter((fav) => `${fav.id}` !== `${action.payload}`) //con backtiks se hace la comparacion de ambos como string
            };
          
        default:
        return {...state};
     }
    }

    export default reducer;
