 export const validate = (userData) => {
        let errors = {};
    if (!userData.email) {
        setErrors({...errors, email: "El campo de correo electrónico es obligatorio"})
      } else if (!/\S+@\S+\.\S+/.test(userData.email)) {
        setErrors({...errors, email: "Debes ingresar un correo electrónico válido"})
      } else if (userData.email.length > 35) {
        setErrors({...errors, email: "El correo electrónico debe tener menos de 35 caracteres"})
      } else {
        setErrors({...errors, email: ""})
      }
        
      if (!/\d/.test(userData.password)) {
          setErrors({...errors, password: "La contraseña debe incluir al menos un número"})
        } else if (!/.{6,10}/.test(userData.password)){
          setErrors({...errors, password: "La contraseña debe tener entre 6 y 10 caracteres"})
        }else {
            setErrors({...errors, password: ""})

        }
        return errors;
    };
