import {useState} from 'react';
import style from './SearchBar.module.css';

export default function SearchBar({onSearch}) {

   const [id, setId] = useState('');
   
   
   const handleChange = (event) => {
      const value = event.target.value;
      if (value < 1 || value > 826) {
        alert('Comienza por 1 y busca hasta el 826!');
        event.target.value = '';
         return;
      }
      setId(value);
    };

    const handleChangeRandom = () => {
      return (Math.floor(Math.random() * 826) + 1).toString()
    };
    

   return (
      <>
<div className={style.search__container}>
  <div className={style.input__container}>
    <input type='text' onChange={handleChange} className={style.search__input} placeholder='elije a tu personaje'/>
    <button onClick={()=>{onSearch(id)}} className={style.search__button}>Agregar!</button>
  </div>
  <button onClick={()=>{onSearch(handleChangeRandom())}} className={style.random__button}>
    Agrega un personaje aleatorio
  </button>
</div>

      </>
         );
      };
         
