import React from "react";
import { Link } from "react-router-dom";
import "./Card.css";

export default function Card({
  id,
  name,
  status,
  species,
  gender,
  origin,
  image,
  onClose,
}) {
  return (
   <div className="container">
    <div className="card">
      <button className="close-button" onClick={() => onClose(id)}>
        X
      </button>
      <Link to={`/detail/${id}`}>
        <h2 className="tooltip">
          {name}
          <span className="tooltiptext">¿Quieres saber más sobre {name}?</span>
        </h2>
      </Link>
      <div className="details">
        <h3>{status}</h3>
        <h3>{species}</h3>
        <h3>{gender}</h3>
        <h3>{origin}</h3>
      </div>
      <img className="image" src={image} alt="" />
    </div>
   </div>
  );
}
