import React from 'react';
import { Link } from 'react-router-dom';
      
export default function Card({ id, name, status, species, gender, origin, image, onClose }) {
   return (
      <div>
      <button onClick={() => onClose(id)}>X</button>
      <Link to={`/detail/${id}`}>
         <h2 title={`¿Quieres saber más sobre ${name}?`}>{name}</h2>
      </Link>
      <h2>{status}</h2>
      <h2>{species}</h2>
      <h2>{gender}</h2>
      <h2>{origin}</h2>
      <img src={image} alt="" />
      <style>
         {`
            h2, img {
            display: block;
            margin-bottom: 10px;
            }
            
            h2:first-child {
            margin-top: 0;
            }
            
            [title]::after {
            content: attr(title);
            background-color: #333;
            color: #fff;
            padding: 10px;
            font-size: 18px;
            position: absolute;
            z-index: 1;
            left: 50%;
            transform: translateX(-50%);
            white-space: nowrap;
            opacity: 0;
            transition: opacity 0.3s ease-in-out;
            }
            
            [title]:hover::after {
            opacity: 1;
            }
         `}
      </style>
      </div>
   );
}
